INSERT INTO universos (nombre, creador, fecha, descripcion, imagen) VALUES
('Dragon Ball', 'Akira Toriyama', '1984', 'Una serie de anime y manga que sigue las aventuras de Goku, un joven guerrero que busca las bolas de dragón y entrena para convertirse en el más fuerte del universo.', 'https://static.wikia.nocookie.net/dragonball/images/1/12/Dragon_Ball_Super_official_logo.png/revision/latest?cb=20161222173214&path-prefix=es'),
